// Generated by Copilot
// Script to apply the database schema to Supabase
// This script reads the schema.sql file and applies it to your Supabase database

import { createClient } from '@supabase/supabase-js'
import fs from 'fs'
import path from 'path'
import { fileURLToPath } from 'url'

const __filename = fileURLToPath(import.meta.url)
const __dirname = path.dirname(__filename)

// Load environment variables
import dotenv from 'dotenv'
dotenv.config({ path: '.env.local' })

const supabaseUrl = process.env.NEXT_PUBLIC_SUPABASE_URL
const supabaseServiceKey = process.env.SUPABASE_SERVICE_ROLE_KEY

if (!supabaseUrl || !supabaseServiceKey) {
  console.error('âŒ Missing Supabase environment variables')
  console.error('Make sure NEXT_PUBLIC_SUPABASE_URL and SUPABASE_SERVICE_ROLE_KEY are set in .env.local')
  process.exit(1)
}

// Create Supabase client with service role key
const supabase = createClient(supabaseUrl, supabaseServiceKey)

async function applySchema() {
  console.log('ğŸš€ Applying Database Schema to Supabase...\n')

  try {
    // Read the schema file
    const schemaPath = path.join(__dirname, 'supabase', 'schema.sql')
    const schemaSQL = fs.readFileSync(schemaPath, 'utf8')

    console.log('ğŸ“– Reading schema from:', schemaPath)
    console.log('ğŸ“ Schema size:', schemaSQL.length, 'characters\n')

    // Split the schema into individual statements
    const statements = schemaSQL
      .split(';')
      .map(stmt => stmt.trim())
      .filter(stmt => stmt.length > 0 && !stmt.startsWith('--'))

    console.log('ğŸ“ Found', statements.length, 'SQL statements to execute\n')

    let successCount = 0
    let errorCount = 0
    const errors = []

    // Execute each statement
    for (let i = 0; i < statements.length; i++) {
      const statement = statements[i] + ';'
      
      try {
        console.log(`â³ Executing statement ${i + 1}/${statements.length}...`)
        
        // Use the RPC function to execute raw SQL
        const { data, error } = await supabase.rpc('exec_sql', { sql: statement })
        
        if (error) {
          // Some errors are expected (like "already exists" errors)
          if (error.message.includes('already exists') || 
              error.message.includes('duplicate key') ||
              error.message.includes('relation') && error.message.includes('already exists')) {
            console.log(`âš ï¸  Skipped (already exists):`, error.message.substring(0, 100) + '...')
          } else {
            console.error(`âŒ Error in statement ${i + 1}:`, error.message.substring(0, 200) + '...')
            errors.push({ statement: i + 1, error: error.message })
            errorCount++
          }
        } else {
          console.log(`âœ… Statement ${i + 1} executed successfully`)
          successCount++
        }
      } catch (err) {
        console.error(`âŒ Exception in statement ${i + 1}:`, err.message)
        errors.push({ statement: i + 1, error: err.message })
        errorCount++
      }
    }

    console.log('\nğŸ¯ Schema Application Results:')
    console.log('=' .repeat(40))
    console.log(`âœ… Successful statements: ${successCount}`)
    console.log(`âŒ Failed statements: ${errorCount}`)
    
    if (errors.length > 0) {
      console.log('\nâŒ Errors encountered:')
      errors.forEach((err, index) => {
        console.log(`${index + 1}. Statement ${err.statement}: ${err.error.substring(0, 150)}...`)
      })
    }

    if (errorCount === 0) {
      console.log('\nğŸ‰ Schema applied successfully! Your database is ready.')
    } else if (successCount > 0) {
      console.log('\nâš ï¸  Schema applied with some errors. Check the errors above.')
      console.log('   Many "already exists" errors are normal if running this script multiple times.')
    } else {
      console.log('\nğŸ’¥ Schema application failed. Check your Supabase connection and permissions.')
    }

  } catch (error) {
    console.error('ğŸ’¥ Fatal error applying schema:', error.message)
    process.exit(1)
  }
}

