const SCRAPER_URL = 'http://localhost:3000/api/scrape'
const CRON_SECRET = 'your-secure-secret-key-here-change-this-in-production'

async function testScraper() {
  console.log('ğŸš€ Testing Web Scraper API...\n')

  try {
    const response = await fetch(SCRAPER_URL, {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${CRON_SECRET}`,
        'Content-Type': 'application/json'
      }
    })

    console.log(`ğŸ“Š Response Status: ${response.status}`)

    if (!response.ok) {
      const error = await response.text()
      console.error('âŒ Error:', error)
      return
    }

    const data = await response.json()
    
    console.log('\nâœ… Success! Scraper Results:')
    console.log('==========================================')
    console.log(`ğŸ“ˆ Total Jobs Found: ${data.summary.totalJobsFound}`)
    console.log(`ğŸ’¾ Total Jobs Inserted: ${data.summary.totalJobsInserted}`)
    console.log(`âš ï¸  Total Errors: ${data.summary.totalErrors}`)
    
    console.log('\nğŸ“‹ Source Breakdown:')
    console.log('------------------------------------------')
    
    data.results.forEach(result => {
      console.log(`\n${result.source}:`)
      console.log(`  â€¢ Found: ${result.jobsFound} jobs`)
      console.log(`  â€¢ Inserted: ${result.jobsInserted} jobs`)
      if (result.errors.length > 0) {
        console.log(`  â€¢ Errors: ${result.errors.join(', ')}`)
      }
    })

    console.log('\nğŸ“ Message:', data.message)

  } catch (error) {
    console.error('âŒ Network Error:', error.message)
  }
}

